import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";

function EditCourse() {
  const { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState("");
  const [professorOptions, setProfessorOptions] = useState([]);
  const [professorIdState, setProfessorId] = useState(professorId);

  const [validInput, setValidInput] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
          setProfessorId(result.professor.id);
        });
      });
    }

    let urlGetAllProfessors = HOST + "/professors";
    fetch(urlGetAllProfessors).then((response) => {
      response.json().then((result) => {
        let helpList = [];
        result.map((p) =>
          helpList.push({
            value: p.id,
            label: p.firstname + " " + p.lastname + ", " + p.academicTitle,
          })
        );
        setProfessorOptions(helpList);
      });
    });
  }, []);

  function changeName(event) {
    setName(event.target.value);
    checkValidInput();
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
    checkValidInput();
  }

  function changeEcte(event) {
    setEcts(event.target.value);
    checkValidInput();
  }

  function changeProfessor(event) {
    professorId = event.target.value;
    checkValidInput();
  }

  function checkValidInput() {
    if (
      name.trim() == "" ||
      courseCode.trim() == "" ||
      ects <= 0 ||
      !professorId
    ) {
      setValidInput(false);
    } else setValidInput(true);
  }

  function handleSubmit() {
    console.log("\n\n\n\n");
    console.error("EDIT COURSE -> handleSubmit \n POPRAVITI professorId");
    console.log("\n\n\n\n");
    professorId = 1;

    let url = HOST + "/professors/" + professorId + "/courses";
    let course = {};
    course["name"] = name;
    course["courseCode"] = courseCode;
    course["ects"] = ects;
    let methodName = "POST";
    if (courseId) {
      course["id"] = courseId;
      url = HOST + "/courses";
      methodName = "PUT";
    }
    let requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(course),
    };

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Name:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={changeName}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Course code:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="courseCode"
                value={courseCode}
                onChange={changeCourseCode}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">ECTS:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="ects"
                value={ects}
                onChange={changeEcte}
              />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Professor:</div>
            <div className="col-4">
              <Select options={professorOptions} onChange={changeProfessor} />

              <select className="form-select" onChange={changeProfessor}>
                <option selected>Select professor</option>
                {allProfessors.map((e, i) => (
                  <option key={i} value={e.id}>
                    {e.firstname + " " + e.lastname + ", " + e.academicTitle}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <Link to="/courses" onClick={handleSubmit}>
            <button>Save</button>
          </Link>
        </div>
      </form>
    </div>
  );
}

export default EditCourse;
