import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";

function EditCourse() {
  let { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState("");
  const [allProfessors, setAllProfessors] = useState([]);

  const [validInput, setValidInput] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
        });
      });
    }

    let urlGetAllProfessors = HOST + "/professors";
    fetch(urlGetAllProfessors).then((response) => {
      response.json().then((result) => {
        setAllProfessors(result);
      });
    });
  }, []);

  function changeName(event) {
    setName(event.target.value);
    checkValidInput();
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
    checkValidInput();
  }

  function changeEcte(event) {
    setEcts(event.target.value);
    checkValidInput();
  }

  function changeProfessor(event) {
    professorId = event.target.value;
    checkValidInput();
  }

  function checkValidInput() {
    if (
      name.trim() == "" ||
      courseCode.trim() == "" ||
      ects <= 0 ||
      !professorId
    ) {
      setValidInput(false);
    } else setValidInput(true);
  }

  function handleSubmit() {
    let url = HOST + "/professors/" + professorId + "/courses";

    console.log("\n\n\n" + url + "\n\n");

    let course = {};
    course["name"] = name;
    course["courseCode"] = courseCode;
    course["ects"] = ects;
    let methodName = "POST";
    if (courseId) {
      course["id"] = courseId;
      url = url + "/" + courseId;
      methodName = "PUT";
    }

    console.log("\n\n\n" + course + "\n\n\n");

    let requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(course),
    };

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Name:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={changeName}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Course code:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="courseCode"
                value={courseCode}
                onChange={changeCourseCode}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">ECTS:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="ects"
                value={ects}
                onChange={changeEcte}
              />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Professor:</div>
            <div className="col-4">
              <select className="form-select" onChange={changeProfessor}>
                <option selected>Select professor</option>
                {allProfessors.map((e, i) => (
                  <option
                    key={i}
                    value={e.id}
                    selected={professorId && e.id == professorId}
                  >
                    {e.firstname + " " + e.lastname + ", " + e.academicTitle}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {validInput && (
            <Link to="/courses" onClick={handleSubmit}>
              <button>Save</button>
            </Link>
          )}
          {!validInput && <button className="cursor-na">Save</button>}
        </div>
      </form>
    </div>
  );
}

export default EditCourse;
