import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { useParams } from "react-router-dom";

function EditCourse() {
  const { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState(0);
  const [professor, setProfessor] = useState(null);

  const [requiredField, setRequiredField] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
          setProfessor(result.professor);
        });
      });
    } else if (professorId) {
      let url = HOST + "/professors/" + professorId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setProfessor(result);
        });
      });
    }
  }, []);

  function changeName(event) {
    setName(event.target.value);
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
  }

  function changeEcte(event) {
    setEcts(event.target.value);
  }

  function handleSubmit() {
    if (name.trim() == "" || courseCode.trim() == "" || ects < 1) {
      setRequiredField(true);
      return false;
    }

    let url = HOST + "/professors/" + professorId + "/courses";
    let course = {};
    course["name"] = name;
    course["courseCode"] = courseCode;
    course["ects"] = ects;
    if (courseId) {
      course["id"] = courseId;
      url = url + "/" + courseId;
    }

    let requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(course),
    };

    fetch(url, requestOptions)
      .then((response) => response.json())
      .then((data) => console.log("data"));

    return true;
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Name:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={changeName}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Course code:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="courseCode"
                value={courseCode}
                onChange={changeCourseCode}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">ECTS:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="ects"
                value={ects}
                onChange={changeEcte}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Professors:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="professor"
                value={
                  professor
                    ? professor.firstname +
                      " " +
                      professor.lastname +
                      ", " +
                      professor.academicTitle
                    : " "
                }
                readOnly
              />
            </div>
          </div>
          {requiredField && (
            <div className="text-danger">Please fill in all the fields!</div>
          )}

          <button onClick={handleSubmit}>Save</button>
        </div>
      </form>
    </div>
  );
}

export default EditCourse;
