import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";

function EditProfessor() {
  const { id } = useParams();
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [sex, setSex] = useState("M");
  const [academicTitle, setAcademicTitle] = useState("");

  const [validInput, setValidInput] = useState(false);

  useEffect(() => {
    if (id) {
      let url = HOST + "/professors/" + id;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setFirstname(result.firstname);
          setLastname(result.lastname);
          setAcademicTitle(result.academicTitle);
          setSex(result.sex);
        });
      });
    }
  }, []);

  function changeFirstname(event) {
    setFirstname(event.target.value);
  }

  function changeLastname(event) {
    setLastname(event.target.value);
  }

  function changeAcademicTitle(event) {
    setAcademicTitle(event.target.value);
  }

  function changeSex(event) {
    setSex(event.target.value);
  }

  function handleSubmit() {
    /*
    if (
      firstname.trim() === "" ||
      lastname.trim() === "" ||
      academicTitle.trim() === ""
    ) {
      setRequiredField(true);
      return false;
    }
    */

    let url = HOST + "/professors";

    let professor = {};
    professor["firstname"] = firstname;
    professor["lastname"] = lastname;
    professor["sex"] = sex;
    professor["academicTitle"] = academicTitle;

    let methodName = "POST";
    if (id) {
      methodName = "PUT";
      professor["id"] = id;
    }

    const requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(professor),
    };

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Professor details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Firstname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="firstname"
                value={firstname}
                onChange={changeFirstname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Lastname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="lastname"
                value={lastname}
                onChange={changeLastname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Academic title:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="academicTitle"
                value={academicTitle}
                onChange={changeAcademicTitle}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Sex:</div>
            <div className="col-4" onChange={changeSex}>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="M"
                  defaultChecked={sex.trim == "M"}
                />
                <label className="form-check-label">Man</label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="Z"
                  defaultChecked={sex.trim == "Z"}
                />
                <label className="form-check-label">Woman</label>
              </div>
            </div>
          </div>

          {validInput && (
            <Link to="/professors" onClick={handleSubmit}>
              SAVE
            </Link>
          )}

          {validInput == false && "SAVE"}
        </div>
      </form>
    </div>
  );
}

export default EditProfessor;
