import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";
import Select from "react-select";

function EditCourse() {
  const { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState("");
  const [professor, setProfessor] = useState(null);
  const [allProfessor, setAllProfessor] = useState([]);
  const [professorOptions, setProfessorOptions] = useState([]);
  const [professorIdState, setProfessorId] = useState(professorId);
  const [professorDefaultValueIndex, setProfessorDefaultValueIndex] =
    useState(-1);

  const [validInput, setValidInput] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
          setProfessorId(result.professor.id);
          setProfessor(result.professor);

          console.log("\n\n\n\n\n");
          console.log(professor);
          console.log("\n\n\n\n\n");
        });
      });
    }

    let urlGetAllProfessors = HOST + "/professors";
    fetch(urlGetAllProfessors).then((response) => {
      response.json().then((result) => {
        setAllProfessor(result);
        let helpList = [];
        result.map((p) =>
          helpList.push({
            value: p.id,
            label: p.firstname + " " + p.lastname + ", " + p.academicTitle,
          })
        );
        setProfessorOptions(helpList);

        result.map((p, i) => {
          if (p.id == professorId) {
            setProfessorDefaultValueIndex(i);
          }
        });
      });
    });
  }, []);

  function changeName(event) {
    setName(event.target.value);
    checkValidInput();
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
    checkValidInput();
  }

  function changeEcte(event) {
    setEcts(event.target.value);
    checkValidInput();
  }

  function changeProfessor(event) {
    setProfessorId(event.value);
    allProfessor.map((p, i) => {
      if (p.id == professorId) {
        setProfessor(p);
        console.log("\n\n\nEEEE: " + professor + "\n\n\n\n");
      }
    });
    checkValidInput();
  }

  function checkValidInput() {
    if (
      name.trim() != "" &&
      courseCode.trim() != "" &&
      ects > 0 &&
      professorIdState
    ) {
      setValidInput(true);
    } else setValidInput(false);
  }

  function handleSubmit() {
    let url = HOST + "/professors/" + professorIdState + "/courses";
    let course = {};
    course["name"] = name;
    course["courseCode"] = courseCode;
    course["professor"] = professor;
    course["ects"] = ects;
    let methodName = "POST";
    if (courseId) {
      course["id"] = courseId;
      url = HOST + "/courses";
      methodName = "PUT";
    }

    console.log("\n\n\n\n\n");
    console.log(course);
    console.log("\n\n\n\n\n");

    let requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(course),
    };

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Name:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={changeName}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Course code:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="courseCode"
                value={courseCode}
                onChange={changeCourseCode}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">ECTS:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="ects"
                value={ects}
                onChange={changeEcte}
              />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Professor:</div>
            <div className="col-4">
              <Select
                options={professorOptions}
                onChange={changeProfessor}
                defaultValue={professorOptions[professorDefaultValueIndex]}
              />
            </div>
          </div>

          <Link to="/courses" onClick={handleSubmit}>
            <button>Save</button>
          </Link>
        </div>
      </form>
    </div>
  );
}

export default EditCourse;
