import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { useParams } from "react-router-dom";

function EditProfessor() {
  const { id } = useParams();
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [sex, setSex] = useState("");
  const [academicTitle, setAcademicTitle] = useState("");

  useEffect(() => {
    if (!id) {
      return null;
    }

    let url = HOST + "/professors/" + id;
    fetch(url).then((response) => {
      response.json().then((result) => {
        setFirstname(result.firstname);
        setLastname(result.lastname);
        setAcademicTitle(result.academicTitle);
        {
          result.sex === "M" ? setSex("Man") : setSex("Woman");
        }
      });
    });
  }, []);

  return (
    <div>
      <h3 align="center">Professor details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Firstname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="firstname"
                value={firstname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Lastname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="lastname"
                value={lastname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Academic title:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="academicTitle"
                value={academicTitle}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Sex:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="sex"
                value={sex}
              />
            </div>
          </div>
        </div>
      </form>
    </div>
  );
}

export default EditProfessor;
