import "../bootstrap.css";
import ProfessorDetails from "./ProfessorDetails";
import { useEffect, useState } from "react";

function Professors() {
  const [laoding, setLoading] = useState(false);
  const [professors, setProfessors] = useState([]);
  const [professorInfo, setProfessorInfo] = useState(null);

  useEffect(() => {
    setLoading(true);

    //  let url = 'https://jsonplaceholder.typicode.com/users';
    let url = "http://localhost:8080/professors";

    fetch(url)
      .then((response) => {
        response.json().then((result) => {
          console.log(response);
          setProfessors(result);
        });
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <div className="container">
      <h3 className="text-center">All professors</h3>
      {laoding && <div> loading..... </div>}
      <table class="table table-bordered">
        <thead>
          <tr>
            <th scope="col">Firstname</th>
            <th scope="col">Lastname</th>
            <th scope="col">Sex</th>
            <th scope="col">Academic title</th>
            <th scope="col">More info</th>
          </tr>
        </thead>
        <tbody>
          {professors.map((e, i) => (
            <tr key={i}>
              <td>{e.firstname}</td>
              <td>{e.lastname}</td>
              <td>{e.sex === "M" ? "Man" : "Woman"}</td>
              <td>{e.academicTitle}</td>
              <td className="text-center">
                <img
                  src={require("../Images/more-info.jpg")}
                  alt="More info"
                  title="More informations"
                  className="btn"
                  onClick={() => setProfessorInfo(e)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        {professorInfo != null ? (
          <ProfessorDetails professor={professorInfo} />
        ) : (
          <></>
        )}
      </div>
    </div>
  );
}

export default Professors;
