import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import HOST from "../Setup";
import "../bootstrap.css";

function ViewCourseDetails(props) {
  function showSuccessfulExams() {}

  function showFailedExams() {}

  const course = props.course;
  return (
    <div>
      <h3 align="center">Course derails:</h3>
      <br />
      <table className="table container">
        <tbody>
          <tr>
            <td>Course name:</td>
            <td>{course.name}</td>
          </tr>
          <tr>
            <td>Course code:</td>
            <td>{course.courseCode}</td>
          </tr>
          <tr>
            <td>ECTS:</td>
            <td>{course.ects}</td>
          </tr>
          <tr>
            <td>Professor:</td>
            <td>
              {course.professor.firstname} {course.professor.lastname},{" "}
              {course.professor.academicTitle}
            </td>
          </tr>
          <tr>
            <td>Average grade:</td>
            <td>{course.averageGrade}</td>
          </tr>
          <tr>
            <td>Number of successful exams:</td>
            <td>
              {course.successfulExams.length}{" "}
              <button className="btn btn-link">Show list</button>
            </td>
          </tr>
          <tr>
            <td>Number of failed exams:</td>
            <td>
              {course.failedExams.length}{" "}
              <button className="btn btn-link">Show list</button>
            </td>
          </tr>
        </tbody>
      </table>

      <div id="tableFailedExams" className="container">
        <h4 align="center">Failed exams:</h4>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Student</th>
              <th scope="col">Index number</th>
              <th scope="col">Date</th>
              <th scope="col">Grade</th>
            </tr>
          </thead>
          <tbody>
            {course.failedExams.map((e, i) => (
              <tr key={i}>
                <td>
                  {e.student.firstname} {e.student.lastname}
                </td>
                <td>{e.student.indexNumber}</td>
                <td>{e.date}</td>
                <td>{e.grade}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div id="tableSuccessfulExams" className="container">
        <h4 align="center">Successful exams:</h4>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Student</th>
              <th scope="col">Index number</th>
              <th scope="col">Date</th>
              <th scope="col">Grade</th>
            </tr>
          </thead>
          <tbody>
            {course.successfulExams.map((e, i) => (
              <tr key={i}>
                <td>
                  {e.student.firstname} {e.student.lastname}
                </td>
                <td>{e.student.indexNumber}</td>
                <td>{e.date}</td>
                <td>{e.grade}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function CourseDetails() {
  const [courseDetails, setCourseDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    let url = HOST + "/courses/" + id;
    fetch(url)
      .then((response) => {
        response.json().then((result) => {
          setCourseDetails(result);
        });
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <div>{courseDetails && <ViewCourseDetails course={courseDetails} />}</div>
  );
}

export default CourseDetails;
