import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { useParams } from "react-router-dom";

function EditCourse() {
  const { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState(0);
  const [professor, setProfessor] = useState(null);

  const [requiredField, setRequiredField] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
          setProfessor(result.professor);
        });
      });
    } else {
      let url = HOST + "/professors/" + professorId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setProfessor(result);
        });
      });
    }
  }, []);

  function changeName(event) {
    setName(event.target.value);
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
  }

  function changeEcte(event) {
    setEcts(event.target.value);
  }

  function handleSubmit() {
    if (name.trim() == "" || courseCode.trim() == "" || ects < 1) {
      setRequiredField(true);
      return false;
    }

    return true;
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
    </div>
  );
}

export default EditCourse;
