import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { useParams } from "react-router-dom";

function EditProfessor() {
  const { id } = useParams();
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [sex, setSex] = useState("M");
  const [academicTitle, setAcademicTitle] = useState("");

  const [requiredField, setRequiredField] = useState(false);

  useEffect(() => {
    if (id) {
      let url = HOST + "/professors/" + id;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setFirstname(result.firstname);
          setLastname(result.lastname);
          setAcademicTitle(result.academicTitle);
          setSex(result.sex);
        });
      });
    }
  }, []);

  /*
  useEffect(() => {
    let url = "http://localhost:8080/professors";

    const professors = [
      {
        id: 3,
        firstname: "Zana",
        lastname: "Crnogorac",
        sex: "Z",
        academicTitle: "asistent",
      },
      {
        id: 4,
        firstname: "Mirko",
        lastname: "Markovic",
        sex: "M",
        academicTitle: "asistent",
      },
    ];

    professors.map((professor) => {
      const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(professor),
      };

      fetch(url, requestOptions)
        .then((response) => response.json())
        .then((data) => console.log("seper"))
        .catch(() => console.log("PROBLEM"))
        .finally(() => {
          console.log("ZAvrseno");
        });
    });
  }, []);
*/
  function changeFirstname(event) {
    setFirstname(event.target.value);
  }

  function changeLastname(event) {
    setLastname(event.target.value);
  }

  function changeAcademicTitle(event) {
    setAcademicTitle(event.target.value);
  }

  function changeSex(event) {
    setSex(event.target.value);
  }

  async function handleSubmit() {
    if (
      firstname.trim() === "" ||
      lastname.trim() === "" ||
      academicTitle.trim() === ""
    ) {
      setRequiredField(true);
      return false;
    }

    /*
    let professor = {
      id: 1,
      firstname: firstname,
      lastname: lastname,
      sex: sex,
      academicTitle: academicTitle,
    };
    */
    /*
    let professor = {};
    professor["id"] = 1;
    professor["firstname"] = "V";
    professor["lastname"] = "J";
    professor["sex"] = "M";
    professor["academicTitle"] = "Asistent";

    let requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(professor),
    };

    fetch(url, requestOptions)
      .then((response) => response.json())
      .then((data) => console.log("super"))
      .catch(() => console.log("PROBLEM!!!!"))
      .finally(() => console.log("ZAVRSENO"));
    */

    let url = "http://localhost:8080/professors";

    const professors = [
      {
        id: 3,
        firstname: "Zana",
        lastname: "Crnogorac",
        sex: "Z",
        academicTitle: "asistent",
      },
      {
        id: 4,
        firstname: "Mirko",
        lastname: "Markovic",
        sex: "M",
        academicTitle: "asistent",
      },
    ];

    professors.map((professor) => {
      const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(professor),
      };

      fetch(url, requestOptions)
        .then((response) => response.json())
        .then((data) => console.log("seper"))
        .catch(() => console.log("PROBLEM"))
        .finally(() => {
          console.log("ZAvrseno");
        });
    });

    return true;
  }

  return (
    <div>
      <h3 align="center">Professor details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Firstname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="firstname"
                value={firstname}
                onChange={changeFirstname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Lastname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="lastname"
                value={lastname}
                onChange={changeLastname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Academic title:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="academicTitle"
                value={academicTitle}
                onChange={changeAcademicTitle}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Sex:</div>
            <div className="col-4" onChange={changeSex}>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="M"
                  defaultChecked={sex === "M"}
                />
                <label className="form-check-label">Man</label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="Z"
                  defaultChecked={sex === "Z"}
                />
                <label className="form-check-label">Woman</label>
              </div>
            </div>
          </div>
          {requiredField && (
            <div className="text-danger">Please fill in all the fields!</div>
          )}
          <button onClick={handleSubmit}>Save</button>
        </div>
      </form>
    </div>
  );
}

export default EditProfessor;
