import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";
import Select from "react-select";

function EditExam() {
  let { studentId } = useParams();
  const { courseId } = useParams();
  const [allCourses, setAllCourses] = useState([]);
  const [allStudents, setAllStudents] = useState([]);
  const [course, setCourse] = useState(null);
  const [student, setStudent] = useState(null);

  const [validInput, setValidInput] = useState(false);

  let courseOptions = [];
  let studentOptions = [];

  useEffect(() => {
    let url = HOST + "/courses";
    fetch(url).then((response) => {
      response.json().then((result) => {
        setAllCourses(result);
        result.map((c, i) => {
          courseOptions.push({
            value: c.id,
            label: c.courseCode + ", " + c.name,
          });
        });
      });
    });

    if (courseId)
      allCourses.map((e, i) => {
        if (e.id === courseId) setCourse(e);
      });

    url = HOST + "/students";
    fetch(url).then((response) => {
      response.json().then((result) => {
        setAllStudents(result);
        result.map((s, i) => {
          studentOptions.push({
            value: s.id,
            label: s.indexNumber + ", " + s.firstname + " " + s.lastname,
          });
        });
      });

      if (studentId)
        allCourses.map((e, i) => {
          if (e.id === studentId) setStudent(e);
        });
    });
  }, []);

  function checkValidInput() {}

  function onChangeCourse(event) {
    allCourses
      .filter((c) => c.id == event.target.value)
      .map((c) => setCourse(c));

    console.log(course);
  }

  function onChangeStudent(event) {
    allStudents
      .filter((s) => s.id == event.target.value)
      .map((s) => setStudent(s));

    console.log(student);
  }

  return (
    <div>
      <h3 align="center">Exam details:</h3>
      <form>
        <div className="container">
          <div className="row ">
            <div className="col-4">Student:</div>
            <div className="col-4">
              <select className="form-select" onChange={onChangeStudent}>
                <option>Select student</option>
                {allStudents.map((e, i) => (
                  <option key={i} value={e.id}>
                    {e.firstname + " " + e.lastname + ", " + e.indexNumber}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Course:</div>
            <div className="col-4">
              <Select options={courseOptions} onChange={onChangeCourse} />

              <select defaultValue={1} className="form-select">
                <option>Select course</option>
                {allCourses.map((e, i) => (
                  <option key={i} value={e.id}>
                    {e.courseCode + ", " + e.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Date:</div>
            <div className="col-4">
              <input type="date" className="form-control" name="dateExam" />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Grade:</div>
            <div className="col-4">
              <input type="text" className="form-control" name="grade" />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Test:</div>
            <div className="col-4">
              <Select options={options} defaultValue={options[1]} />
            </div>
          </div>

          <Link to="/exams">
            <button>Save</button>
          </Link>
        </div>
      </form>
    </div>
  );
}

export default EditExam;
