import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { useParams } from "react-router-dom";

function EditProfessor() {
  const { id } = useParams();
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [sex, setSex] = useState("M");
  const [academicTitle, setAcademicTitle] = useState("");

  const [requiredField, setRequiredField] = useState(false);

  useEffect(() => {
    if (!id) {
      return null;
    }

    let url = HOST + "/professors/" + id;
    fetch(url).then((response) => {
      response.json().then((result) => {
        setFirstname(result.firstname);
        setLastname(result.lastname);
        setAcademicTitle(result.academicTitle);
        setSex(result.sex);
      });
    });
  }, []);

  function changeFirstname(event) {
    setFirstname(event.target.value);
  }

  function changeLastname(event) {
    setLastname(event.target.value);
  }

  function changeAcademicTitle(event) {
    setAcademicTitle(event.target.value);
  }

  function changeSex(event) {
    setSex(event.target.value);
  }

  function handleSubmit() {
    let err = false;
    if (
      firstname.trim() === "" ||
      lastname.trim() === "" ||
      academicTitle.trim() === ""
    ) {
      setRequiredField(true);
      return false;
    }

    const url = HOST + "/professors";
    let professor = {
      firstname: firstname,
      lastname: lastname,
      sex: sex,
      academicTitle: academicTitle,
    };

    let requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(professor),
    };

    if (id) {
      professor["id"] = id;
      requestOptions["method"] = "PUT";
    }

    fetch(url, requestOptions)
      .then((response) => response.json())
      .then((data) => console.log("super"))
      .catch(() => console.log("PROBLEM!!!!"))
      .finally(() => console.log("ZAVRSENO"));
    return false;
  }

  return (
    <div>
      <h3 align="center">Professor details:</h3>
      <form onSubmit={handleSubmit}>
        <div className="container">
          <div className="row">
            <div className="col-4">Firstname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="firstname"
                value={firstname}
                onChange={changeFirstname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Lastname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="lastname"
                value={lastname}
                onChange={changeLastname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Academic title:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="academicTitle"
                value={academicTitle}
                onChange={changeAcademicTitle}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Sex:</div>
            <div className="col-4" onChange={changeSex}>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="M"
                  checked={sex === "M"}
                />
                <label className="form-check-label" for="inlineCheckbox1">
                  Man
                </label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="Z"
                  checked={sex === "Z"}
                />
                <label className="form-check-label" for="inlineCheckbox2">
                  Woman
                </label>
              </div>
            </div>
          </div>
          {requiredField && (
            <div className="text-danger">Please fill in all the fields!</div>
          )}
          <input type="submit" value="Submit" />
        </div>
      </form>
    </div>
  );
}

export default EditProfessor;
