import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { useParams } from "react-router-dom";

function EditCourse() {
  const { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState(0);
  const [professor, setProfessor] = useState(null);

  const [requiredField, setRequiredField] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
          setProfessor(result.professor);
        });
      });
    } else {
      let url = HOST + "/professors/" + professorId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setProfessor(result);
        });
      });
    }
  }, []);

  function changeName(event) {
    setName(event.target.value);
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
  }

  function changeEcte(event) {
    setEcts(event.target.value);
  }

  function handleSubmit() {
    if (name.trim() == "" || courseCode.trim() == "" || ects < 1) {
      setRequiredField(true);
      return false;
    }

    /*
    let url = HOST + "/professors/ " + professorId + "/courses";

    const professors = [
      {
        id: 3,
        firstname: "Zana",
        lastname: "Crnogorac",
        sex: "Z",
        academicTitle: "asistent",
      },
      {
        id: 4,
        firstname: "Mirko",
        lastname: "Markovic",
        sex: "M",
        academicTitle: "asistent",
      },
    ];

    professors.map((professor) => {
      const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(professor),
      };

      fetch(url, requestOptions)
        .then((response) => response.json())
        .then((data) => console.log("seper"))
        .catch(() => console.log("PROBLEM"))
        .finally(() => {
          console.log("ZAvrseno");
        });
    });
    */

    return true;
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Name:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={changeName}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Course code:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="courseCode"
                value={courseCode}
                onChange={changeCourseCode}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">ECTS:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="ects"
                value={ects}
                onChange={changeEcte}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Professors:</div>
            <div className="col-4" onChange={changeSex}>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="M"
                  defaultChecked={sex.trim == "M"}
                />
                <label className="form-check-label">Man</label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="Z"
                  defaultChecked={sex.trim == "Z"}
                />
                <label className="form-check-label">Woman</label>
              </div>
            </div>
          </div>
          {requiredField && (
            <div className="text-danger">Please fill in all the fields!</div>
          )}

          <button onClick={handleSubmit}>Save</button>
        </div>
      </form>
    </div>
  );
}

export default EditProfessor;
