import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import HOST from "../Setup";

import "../bootstrap.css";

function ViewProfessorDetails(props) {
  const professor = props.professor;
  return (
    <div>
      <h3 align="center">Professor derails:</h3>
      <br />
      <div class="container">
        <div class="row">
          <div class="col-md-4 offset-md-2">Firstname:</div>
          <div class="col-md-4">{professor.firstname}</div>
        </div>
        <div class="row">
          <div class="col-md-4 offset-md-2">Lastname:</div>
          <div class="col-md-4">{professor.lastname}</div>
        </div>
        <div class="row">
          <div class="col-md-4 offset-md-2">Academic title:</div>
          <div class="col-md-4">{professor.academicTitle}</div>
        </div>
        <div class="row">
          <div class="col-md-4 offset-md-2">Sex:</div>
          <div class="col-md-4">{professor.sex}</div>
        </div>

        <br />
        <h4 align="center">Courses: </h4>
        <br />

        <table class="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">First</th>
              <th scope="col">Last</th>
              <th scope="col">Handle</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
            <tr>
              <th scope="row">2</th>
              <td>Jacob</td>
              <td>Thornton</td>
              <td>@fat</td>
            </tr>
            <tr>
              <th scope="row">3</th>
              <td>Larry</td>
              <td>the Bird</td>
              <td>@twitter</td>
            </tr>
          </tbody>
        </table>

        <div class="row">
          <div class="col-md-4 offset-md-2">Academic title:</div>
          <div class="col-md-4">{professor.academicTitle}</div>
        </div>

        {professor.courses.map((e, i) => (
          <div>{e.name}</div>
        ))}
      </div>
    </div>
  );
}

function ProfessorDetails(props) {
  const [professorDetails, setProfessorDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    let url = HOST + "/professors/" + id;

    console.log(url);

    fetch(url)
      .then((response) => {
        response.json().then((result) => {
          setProfessorDetails(result);
          console.log(professorDetails);
        });
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <h3>
        {professorDetails && (
          <ViewProfessorDetails professor={professorDetails} />
        )}
      </h3>
    </div>
  );
}

export default ProfessorDetails;
