import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";

function EditCourse() {
  const { professorId } = useParams();
  const { courseId } = useParams();
  const [name, setName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [ects, setEcts] = useState("");
  const [professor, setProfessor] = useState(null);
  const [allProfessors, setAllProfessors] = useState([]);

  const [enableSubmit, setEnableSubmit] = useState(false);

  useEffect(() => {
    if (courseId) {
      let url = HOST + "/courses/" + courseId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setName(result.name);
          setCourseCode(result.courseCode);
          setEcts(result.ects);
          setProfessor(result.professor);
        });
      });
    } else if (professorId) {
      let url = HOST + "/professors/" + professorId;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setProfessor(result);
        });
      });
    }

    let urlGetAllProfessors = HOST + "/professors";
    fetch(urlGetAllProfessors).then((response) => {
      response.json().then((result) => {
        setAllProfessors(result);
      });
    });
  }, []);

  function changeName(event) {
    setName(event.target.value);
    validEnableSubmit();
  }

  function changeCourseCode(event) {
    setCourseCode(event.target.value);
    validEnableSubmit();
  }

  function changeEcte(event) {
    setEcts(event.target.value);
    validEnableSubmit();
  }

  function validEnableSubmit() {
    if (name.trim() == "" || courseCode.trim() == "" || ects < 1) {
      setEnableSubmit(false);
    } else setEnableSubmit(true);
  }

  function handleSubmit() {
    let url = HOST + "/professors/" + professorId + "/courses";
    let course = {};
    course["name"] = name;
    course["courseCode"] = courseCode;
    course["ects"] = ects;
    let methodName = "POST";
    if (courseId) {
      course["id"] = courseId;
      url = url + "/" + courseId;
      methodName = "PUT";
    }

    let requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(course),
    };

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Course details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Name:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={changeName}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Course code:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="courseCode"
                value={courseCode}
                onChange={changeCourseCode}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">ECTS:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="ects"
                value={ects}
                onChange={changeEcte}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Professors:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="professor"
                value={
                  professor
                    ? professor.firstname +
                      " " +
                      professor.lastname +
                      ", " +
                      professor.academicTitle
                    : " "
                }
                readOnly
              />
            </div>
          </div>

          <div className="row">
            <div className="col-4">TEST:</div>
            <div className="col-4">
              <select className="custom-select" id="inputGroupSelect02">
                <option selected>Choose...</option>
                <option value="1">One</option>
                <option value="2">Two</option>
                <option value="3">Three</option>
              </select>
            </div>
          </div>

          {enableSubmit && (
            <Link to="courses" onClick={handleSubmit}>
              <button>Save</button>
            </Link>
          )}
          {!enableSubmit && <button className="cursor-na">Save</button>}
        </div>
      </form>
    </div>
  );
}

export default EditCourse;
