import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import HOST from "../Setup";
import "../bootstrap.css";

function ViewCourseDetails(props) {
  function showSuccessfulExams() {}

  function showSuccessfulExams() {}

  const course = props.course;
  return (
    <div>
      <h3 align="center">Course derails:</h3>
      <br />
      <table className="table">
        <tbody>
          <tr>
            <td>Course name:</td>
            <td>{course.name}</td>
          </tr>
          <tr>
            <td>Course code:</td>
            <td>{course.courseCode}</td>
          </tr>
          <tr>
            <td>ECTS:</td>
            <td>{course.ects}</td>
          </tr>
          <tr>
            <td>Professor:</td>
            <td>
              {course.professor.firstname} {course.professor.lastname},{" "}
              {course.professor.academicTitle}
            </td>
          </tr>
          <tr>
            <td>Average grade:</td>
            <td>{course.averageGrade}</td>
          </tr>
          <tr>
            <td>Number of successful exams:</td>
            <td>
              {course.successfulExams.length} <a>Show list</a>{" "}
            </td>
          </tr>
          <tr>
            <td>Number of failed exams:</td>
            <td>
              {course.failedExams.length}{" "}
              <button className="btn btn-link float-right">Show list</button>
            </td>
          </tr>
        </tbody>
      </table>
      {/*
      <br />
      <h4 align="center">Courses: </h4>
      <br />
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Course name</th>
            <th scope="col">Course code</th>
            <th scope="col">More info</th>
          </tr>
        </thead>
        <tbody>
          {professor.courses.map((e, i) => (
            <tr key={i}>
              <td>{e.name}</td>
              <td>{e.courseCode}</td>
              <td>
                <Link to={"/courses/" + e.id}>
                  <MoreInfo />
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
          */}
    </div>
  );
}

function CourseDetails() {
  const [courseDetails, setCourseDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    let url = HOST + "/courses/" + id;
    fetch(url)
      .then((response) => {
        response.json().then((result) => {
          setCourseDetails(result);
        });
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <div>{courseDetails && <ViewCourseDetails course={courseDetails} />}</div>
  );
}

export default CourseDetails;
