import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";
import AddButton from "../Home/AddButton";

function EditStudent() {
  const { id } = useParams();
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [sex, setSex] = useState("M");
  const [indexNumber, setIndexNumber] = useState("");

  const [requiredField, setRequiredField] = useState(false);

  useEffect(() => {
    if (id) {
      let url = HOST + "/students/" + id;
      fetch(url).then((response) => {
        response.json().then((result) => {
          setFirstname(result.firstname);
          setLastname(result.lastname);
          setIndexNumber(result.indexNumber);
          setSex(result.sex);
        });
      });
    }
  }, []);

  function changeFirstname(event) {
    setFirstname(event.target.value);
  }

  function changeLastname(event) {
    setLastname(event.target.value);
  }

  function changeIndexNumber(event) {
    setIndexNumber(event.target.value);
  }

  function changeSex(event) {
    setSex(event.target.value);
  }

  function handleSubmit() {
    let url = HOST + "/students";

    let student = {};
    student["firstname"] = firstname;
    student["lastname"] = lastname;
    student["sex"] = sex;
    student["indexNumber"] = indexNumber;

    let methodName = "POST";
    if (id) {
      methodName = "PUT";
      student["id"] = id;
    }

    const requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(student),
    };

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Student details:</h3>
      <form>
        <div className="container">
          <div className="row">
            <div className="col-4">Firstname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="firstname"
                value={firstname}
                onChange={changeFirstname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Lastname:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="lastname"
                value={lastname}
                onChange={changeLastname}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Index number:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="indexNumber"
                value={indexNumber}
                onChange={changeIndexNumber}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-4">Sex:</div>
            <div className="col-4" onChange={changeSex}>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="M"
                  defaultChecked={sex.trim == "M"}
                />
                <label className="form-check-label">Man</label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="radio"
                  name="sex"
                  value="Z"
                  defaultChecked={sex.trim == "Z"}
                />
                <label className="form-check-label">Woman</label>
              </div>
            </div>
          </div>
          {validInput && (
            <Link to="/students" onClick={handleSubmit}>
              <AddButton text="Save" />
            </Link>
          )}
        </div>
      </form>
    </div>
  );
}

export default EditStudent;
