import { useEffect, useState } from "react";
import HOST from "../Setup";
import "../bootstrap.css";
import { Link, useParams } from "react-router-dom";
import Select from "react-select";

function EditExam() {
  const { studentId } = useParams();
  const { courseId } = useParams();
  const { examId } = useParams();
  /*
  const [allCourses, setAllCourses] = useState([]);
  const [allStudents, setAllStudents] = useState([]);
  const [course, setCourse] = useState(null);
  const [student, setStudent] = useState(null);
*/
  const [validInput, setValidInput] = useState(false);

  const [courseOptions, setCourseOptions] = useState([]);
  const [studentOptions, setStudentOptions] = useState([]);
  const [courseIdState, setCourseId] = useState(courseId);
  const [studentIdState, setStudentId] = useState(studentId);
  const [grade, setGrade] = useState(0);
  const [date, setDate] = useState(null);

  useEffect(() => {
    let url = HOST + "/courses";
    let helpListCourse = [];
    let helpListStudent = [];
    fetch(url).then((response) => {
      response.json().then((result) => {
        // setAllCourses(result);
        result.map((c, i) => {
          helpListCourse.push({
            value: c.id,
            label: c.courseCode + ", " + c.name,
          });
        });
        setCourseOptions(helpListCourse);
      });
    });

    url = HOST + "/students";
    fetch(url).then((response) => {
      response.json().then((result) => {
        //  setAllStudents(result);
        result.map((s, i) => {
          helpListStudent.push({
            value: s.id,
            label: s.indexNumber + ", " + s.firstname + " " + s.lastname,
          });
          setStudentOptions(helpListStudent);
        });
      });

      /*
      if (studentId)
        allCourses.map((e, i) => {
          if (e.id === studentId) setStudent(e);
        });
        */
    });
  }, []);

  function checkValidInput() {}

  function onChangeCourse(event) {
    setCourseId(event.value);
    checkValidInput();
  }

  function onChangeGrade(event) {
    setGrade(event.target.value);
    checkValidInput();
  }

  function onChangeDate(event) {
    setDate(event.target.value);
    checkValidInput();
  }

  function onChangeStudent(event) {
    setStudentId(event.value);
    checkValidInput();
  }

  function handleSubmit() {
    console.log("\n\n\n" + courseId + "\n\n\n\n");

    let url =
      HOST +
      "/students/" +
      studentIdState +
      "/courses/" +
      courseIdState +
      "/exams";
    let exam = {};
    exam["date"] = date;
    exam["grade"] = grade;
    let methodName = "POST";
    if (examId) {
      exam["id"] = examId;
      url = HOST + "/exams";
      methodName = "PUT";
    }
    let requestOptions = {
      method: methodName,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(exam),
    };

    console.log("\n\n\nURL: " + url + "\n\n\n");

    fetch(url, requestOptions);
  }

  return (
    <div>
      <h3 align="center">Exam details:</h3>
      <form>
        <div className="container">
          <div className="row ">
            <div className="col-4">Student:</div>
            <div className="col-4">
              <Select options={studentOptions} onChange={onChangeStudent} />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Course:</div>
            <div className="col-4">
              <Select options={courseOptions} onChange={onChangeCourse} />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Date:</div>
            <div className="col-4">
              <input
                type="date"
                className="form-control"
                name="dateExam"
                onChange={onChangeDate}
              />
            </div>
          </div>

          <div className="row ">
            <div className="col-4">Grade:</div>
            <div className="col-4">
              <input
                type="text"
                className="form-control"
                name="grade"
                onChange={onChangeGrade}
              />
            </div>
          </div>

          <Link to="/exams" onClick={handleSubmit}>
            <button>Save</button>
          </Link>
        </div>
      </form>
    </div>
  );
}

export default EditExam;
